use aoc_utils::*;
use std::collections::{HashMap, HashSet};

/// This struct is just for being able to pretty print stuff.  It doesn't
/// assist in the puzzle in any other way.
#[derive(Debug)]
pub struct Region {
    pub plant: char,
    pub region: Vec<Point>,
}

impl Region {
    /// The area of a region is the same as the number of plots in the region
    pub fn area(&self) -> usize {
        self.region.len()
    }
    /// The perimeter is calculated by counting the number of sides on each plot
    /// subtracted by the number of neighbors that plot has
    pub fn perimeter(&self) -> usize {
        let mut result = 0;
        for p in &self.region {
            result += 4 - p.count_neighbors(&self.region);
        }
        result
    }
}

impl std::fmt::Display for Region {
    fn fmt(&self, f: &mut std::fmt::Formatter<'_>) -> std::fmt::Result {
        write!(f, "{}:", self.plant)?;
        for plot in &self.region {
            write!(f, " {}", &plot)?;
        }
        write!(f, "")
    }
}

/// Find regions using  breadth-first search. This is a basic flood fill
/// algoorithm. It is assumed the `plots` represent a rectangular matrix.
/// The fill is performed against horizontal and vertical neighbors.
pub fn bfs_region(
    plots: &HashMap<Point, char>,
    plot: &Point,
    plant: char,
    region: &mut HashSet<Point>,
) {
    region.insert(*plot);
    for neighbor in plot
        // For this puzzle, we only look at horizontal and vertical neighbors
        .neighbors_hv()
        .iter()
        .filter(|&p| plots.contains_key(p))
    {
        if plots[neighbor] == plant && !region.contains(neighbor) {
            bfs_region(plots, neighbor, plant, region);
        }
    }
}

